import { Router, json } from 'express'; 
import cors from 'cors' 
import user from '../model/user' 
import product from '../model/product' 
import {LocalStorage} from 'node-localstorage' 
import jwt from 'jsonwebtoken' 
import config from '../config'; 

//defining constants 
const router = Router(); 
var corsOptions={ 
    origin:'*', 
    optionsSuccessStatus:200 
} 

router.route('/user').get((_, response) => { 
    //using mongoose model 
    user.find((err,data)=>{ 
        if(err) 
            throw err 
        else 
            response.json(data) 
    }) 

}); 

router.route('/user').post(json(),cors(corsOptions), (request, response) => { 
    //using mongoose model 
    user.create(request.body,(err,data)=>{ 
        if(err) 
            throw err 
        else 
            response.send(data) 
    }) 
}); 

router.route('/profile').get((request, response) => { 
    let localStorage= new LocalStorage('./Scratch') 
    let token=localStorage.getItem('authToken') 
    console.log(">>>>>>>",token) 

    if(!token) 
        return response.redirect('/') 
    jwt.verify(token,config.secret,(err,decoded)=>{ 
        if(err) 
            response.redirect('/') 
        user.findById(decoded.id,{password:0},(err,user)=>{ 
        if(err) 
            response.redirect('/') 
        if(!user) 
            response.redirect('/') 

        response.render('profile',{user}) 
        }) 

    }) 

}); 


router.route('/adminProfile').get((request, response) => { 
    let localStorage= new LocalStorage('./Scratch') 
    let token=localStorage.getItem('authToken') 
    console.log(">>>>>>>",token) 

    if(!token) 
        return response.redirect('/') 
    jwt.verify(token,config.secret,(err,decoded)=>{ 
        if(err) 
            response.redirect('/') 
        user.findById(decoded.id,{password:0},(err,user)=>{ 
            if(err) 
                response.redirect('/') 
            if(!user) 
                response.redirect('/') 

            response.render('adminProfile',{user}) 
        }) 

    }) 

}); 

router.route('/shoppingList').get((request, response) => { 
    // let localStorage= new LocalStorage('./Scratch') 
    // let token=localStorage.getItem('authToken') 
    // console.log(">>>>>>>",token) 

    // if(!token) 
    //     return response.redirect('/') 
    // jwt.verify(token,config.secret,(err,decoded)=>{ 
    //     if(err) 
    //         response.redirect('/') 
    //     user.findById(decoded.id,{password:0},(err,user)=>{ 
    //         if(err) 
    //             response.redirect('/') 
    //         if(!user) 
    //             response.redirect('/') 

    //         response.render('shoppingList',{user}) 
    //     }) 

    // }) 
    product.find({}, function(err, product){
        if(err) {
          throw err
        } else {
            response.render('shoppingList',{product}) 
        }
    })

}); 

router.route('/userList').get((request, response) => { 
    // let localStorage= new LocalStorage('./Scratch') 
    // let token=localStorage.getItem('authToken') 
    // console.log(">>>>>>>",token) 

    // if(!token) 
    //     return response.redirect('/') 
    // jwt.verify(token,config.secret,(err,decoded)=>{ 
    //     if(err) 
    //         response.redirect('/') 
    //     user.findById(decoded.id,{password:0},(err,user)=>{ 
    //     if(err) 
    //         response.redirect('/') 
    //     if(!user) 
    //         response.redirect('/') 
    //     }) 
    //     user.find({}, function(err, user){
    //         if(err) {
    //           throw err
    //         } else {
    //             response.render('userList',{user}) 
    //         }
    //     })
    // }) 

    user.find({}, function(err, user){
        if(err) {
          throw err
        } else {
            response.render('userList',{user}) 
        }
    })

}); 

router.route('/index').get((request, response) => { 
    response.render('index')

}); 

router.route('/register').get((request, response) => { 
    response.render('register')

}); 

router.route('/addUser').get((request, response) => { 
    response.render('addUser')

}); 

router.route('/addProduct').get((request, response) => { 
    response.render('addProduct')

}); 


export default router;

