// const port = 5000
app.set('port', process.env.PORT||5000)
app.use(express.static(path.join(__dirname,'views/partials')))
const fs = require('fs');

let server = http.createServer(app).listen(app.get('port'), ()=>{
    console.log("express app is up on ", app.get('port'))
})

/* Mongoose */
